<ul class="art-hmenu"><?foreach(_s("SELECT name, url, menu_name FROM pages WHERE public = 1 AND menu = 1 ORDER BY ord ASC") as $r){    ?><li><?    if($r['url'] == $_['url']['path'] || $r['url'] == $_['url']['path'].'/')    {        ?><strong class="active"><span class="l"></span><span class="r"></span><span class="t"><?=($r['menu_name'] != '' ? $r['menu_name'] : $r['name'])?></span></strong><?    } else {        ?><a href="<?=($r['url'] == '' ? '/' : $r['url'])?>" <?=($_['url']['path'] == $r['url'] || $_['url']['path'].'/' == $r['url'] || !$_['url']['path'] && $r['url'] == '/' ? 'class="active"' : '')?> ><span class="l"></span><span class="r"></span><span class="t"><?=($r['menu_name'] != '' ? $r['menu_name'] : $r['name'])?></span></a><?    }    ?></li><?}?></ul>