<ul class="menu_top"><?foreach(_s("SELECT name, url FROM pages WHERE public = 1 AND menu = 1 ORDER BY ord ASC") as $r){    ?><li><?    if($r['url'] == $_['url']['path'] || $r['url'].'/' == $_['url']['path'] || $r['url'] == $_['url']['path'].'/')    {        ?><span class="active"><?=$r['name']?></span><?    } else {        ?><a href="<?=($r['url'] == '' ? '/' : $r['url'])?>" <?=($_['url']['path'] == $r['url'] || $_['url']['path'].'/' == $r['url'] || !$_['url']['path'] && $r['url'] == '/' ? 'class="active"' : '')?> ><?=($r['menu_name'] != '' ? $r['menu_name'] : $r['name'])?></a> <?    }    ?></li><?}?></ul>